import React, { useEffect, useState } from 'react';

function App() {
  const [dogs, setDogs] = useState([]);
  const [selectedDog, setSelectedDog] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('/api/dogs')
      .then(res => res.json())
      .then(data => setDogs(data));
  }, []);

  const viewDog = (id) => {
    fetch(`/api/dogs/${id}`)
      .then(res => res.json())
      .then(data => setSelectedDog(data));
  };

  const buyDog = (id) => {
    fetch(`/api/dogs/buy/${id}`, { method: 'POST' })
      .then(res => res.text())
      .then(msg => {
        setMessage(msg);
        setDogs(dogs.filter(d => d.id !== id));
        setSelectedDog(null);
      });
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>DogBuy - Buy Your Favorite Dog</h1>
      {message && <div style={{ color: 'green' }}>{message}</div>}
      <div style={{ display: 'flex', gap: 40 }}>
        <div>
          <h2>Available Dogs</h2>
          <ul>
            {dogs.map(dog => (
              <li key={dog.id}>
                {dog.name} - ${dog.price}
                <button onClick={() => viewDog(dog.id)} style={{ marginLeft: 10 }}>View</button>
              </li>
            ))}
          </ul>
        </div>
        {selectedDog && (
          <div>
            <h2>{selectedDog.name}</h2>
            <p>{selectedDog.description}</p>
            <p>Price: ${selectedDog.price}</p>
            <button onClick={() => buyDog(selectedDog.id)}>Buy</button>
            <button onClick={() => setSelectedDog(null)} style={{ marginLeft: 10 }}>Back</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
